/**
 * Represents a single card with its fields
 */
public struct AnkiCard {
    public let id: Int64
    public let noteId: Int64
    public let deckId: Int64
    /**
     * Card fields (front, back, extra, etc.)
     */
    public let fields: [String]
    /**
     * Media file references found in the card
     */
    public let mediaReferences: [String]

    // Default memberwise initializers are never public by default, so we
    // declare one manually.
    public init(id: Int64, noteId: Int64, deckId: Int64, 
        /**
         * Card fields (front, back, extra, etc.)
         */fields: [String], 
        /**
         * Media file references found in the card
         */mediaReferences: [String]) {
        self.id = id
        self.noteId = noteId
        self.deckId = deckId
        self.fields = fields
        self.mediaReferences = mediaReferences
    }
}



extension AnkiCard: Equatable, Hashable {
    public static func ==(lhs: AnkiCard, rhs: AnkiCard) -> Bool {
        if lhs.id != rhs.id {
            return false
        }
        if lhs.noteId != rhs.noteId {
            return false
        }
        if lhs.deckId != rhs.deckId {
            return false
        }
        if lhs.fields != rhs.fields {
            return false
        }
        if lhs.mediaReferences != rhs.mediaReferences {
            return false
        }
        return true
    }

    public func hash(into hasher: inout Hasher) {
        hasher.combine(id)
        hasher.combine(noteId)
        hasher.combine(deckId)
        hasher.combine(fields)
        hasher.combine(mediaReferences)
    }
}


