/**
 * Represents a deck with hierarchy support
 */
public struct AnkiDeck {
    public let id: Int64
    /**
     * Full name with "::" separators (e.g., "Parent::Child::Grandchild")
     */
    public let name: String
    /**
     * Just the leaf name (e.g., "Grandchild")
     */
    public let shortName: String

    // Default memberwise initializers are never public by default, so we
    // declare one manually.
    public init(id: Int64, 
        /**
         * Full name with "::" separators (e.g., "Parent::Child::Grandchild")
         */name: String, 
        /**
         * Just the leaf name (e.g., "Grandchild")
         */shortName: String) {
        self.id = id
        self.name = name
        self.shortName = shortName
    }
}



extension AnkiDeck: Equatable, Hashable {
    public static func ==(lhs: AnkiDeck, rhs: AnkiDeck) -> Bool {
        if lhs.id != rhs.id {
            return false
        }
        if lhs.name != rhs.name {
            return false
        }
        if lhs.shortName != rhs.shortName {
            return false
        }
        return true
    }

    public func hash(into hasher: inout Hasher) {
        hasher.combine(id)
        hasher.combine(name)
        hasher.combine(shortName)
    }
}


